import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Typography, Button, Box, List, ListItem, ListItemText } from '@mui/material';

function MachineDetail() {
  const { id } = useParams();
  const [machine, setMachine] = useState(null);
  const [qcForms, setQCForms] = useState([]);
  useEffect(() => {
    axios.get(`/api/machines/${id}`).then(res => setMachine(res.data));
    axios.get(`/api/qc?machineId=${id}`).then(res => setQCForms(res.data));
  }, [id]);
  const markComplete = () => {
    axios.post(`/api/machines/${id}/complete`).then(() => window.location.reload());
  };
  if (!machine) return <div>Loading...</div>;
  return (
    <Box sx={{ mt: 2 }}>
      <Typography variant="h5">{machine.name} Details</Typography>
      <Typography>Master Card: {machine.masterCardInfo || 'N/A'}</Typography>
      <Button href={`/api/machines/${id}/drawing`} target="_blank" sx={{ mt: 1 }}>Download Electric Drawing</Button>
      <Typography sx={{ mt: 2 }}>QC Forms:</Typography>
      <List>
        {qcForms.map(qc => (
          <ListItem key={qc.id}>
            <ListItemText primary={`QC Form #${qc.id} (${qc.status})`} />
            <Button href={`/api/qc/${qc.id}/pdf`} target="_blank">Download PDF</Button>
          </ListItem>
        ))}
      </List>
      {!machine.completed && <Button variant="contained" color="success" onClick={markComplete}>Mark as Complete</Button>}
      <Button component={Link} to="/" sx={{ ml: 2 }}>Back</Button>
    </Box>
  );
}
export default MachineDetail;
