import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { Typography, Button, Box, List, ListItem, ListItemText } from '@mui/material';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import axios from 'axios';

const fetchMachine = async (id) => {
	const { data } = await axios.get(`/api/machines/${id}`);
	return data;
};
const fetchQCForms = async (id) => {
	const { data } = await axios.get(`/api/qc?machineId=${id}`);
	return data;
};
const completeMachineApi = async (id) => {
	const { data } = await axios.post(`/api/machines/${id}/complete`);
	return data;
};

function MachineDetail() {
	const { id } = useParams();
	const queryClient = useQueryClient();
	const { data: machine, isLoading } = useQuery({
		queryKey: ['machine', id],
		queryFn: () => fetchMachine(id)
	});
	const { data: qcForms = [] } = useQuery({
		queryKey: ['qcForms', id],
		queryFn: () => fetchQCForms(id)
	});
	const completeMutation = useMutation({
		mutationFn: () => completeMachineApi(id),
		onSuccess: () => queryClient.invalidateQueries({ queryKey: ['machine', id] })
	});

	if (isLoading || !machine) return <div>Loading...</div>;
	return (
		<Box sx={{ mt: 2 }}>
			<Typography variant="h5">{machine.name} Details</Typography>
			<Typography>Model: {machine.model || 'N/A'}</Typography>
			<Typography>Product No.: {machine.productNo || 'N/A'}</Typography>
			<Typography>Master Card: {machine.masterCardInfo || 'N/A'}</Typography>
			<Button href={`/api/machines/${id}/drawing`} target="_blank" sx={{ mt: 1 }}>Download Electric Drawing</Button>
			<Typography sx={{ mt: 2 }}>QC Forms:</Typography>
			<List>
				{qcForms.map(qc => (
					<ListItem key={qc.id}>
						<ListItemText primary={`QC Form #${qc.id} (${qc.status})`} />
						<Button href={`/api/qc/${qc.id}/pdf`} target="_blank">Download PDF</Button>
					</ListItem>
				))}
			</List>
			{!machine.completed && <Button variant="contained" color="success" onClick={() => completeMutation.mutate()}>Mark as Complete</Button>}
			<Button component={Link} to="/" sx={{ ml: 2 }}>Back</Button>
		</Box>
	);
}
export default MachineDetail;
