	// // Debug: log machines state
	// console.log('machines:', machines);
import React, { useState } from 'react';
import { Grid, Card, CardContent, Typography, Button, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Snackbar } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useDispatch, useSelector } from 'react-redux';
import { setMachines } from './machineSlice';
import axios from 'axios';

const BLOCKS = 12;

const addMachineApi = async (machine) => {
	const { data } = await axios.post('/api/machines', machine);
	return data;
};
	// Debug: confirm component mount
	console.log('MachineGrid mounted');


const fetchMachines = async () => {
	const { data } = await axios.get('/api/machines');
	return data;
};


const completeMachineApi = async (id) => {
	const { data } = await axios.post(`/api/machines/${id}/complete`);
	return data;
};

const deleteMachineApi = async (id) => {
	await axios.delete(`/api/machines/${id}`);
};

function MachineGrid() {
	const navigate = useNavigate();
	const dispatch = useDispatch();
	const machines = useSelector(state => state.machines.list);
	const queryClient = useQueryClient();

	// React Query: fetch machines
	useQuery({
		queryKey: ['machines'],
		queryFn: fetchMachines,
		onSuccess: (data) => {
			console.log('Fetched machines:', data);
			dispatch(setMachines(data));
		},
	});

	// Mutations
	const addMachineMutation = useMutation({
		mutationFn: addMachineApi,
		onSuccess: () => queryClient.invalidateQueries({ queryKey: ['machines'] })
	});
	const completeMachineMutation = useMutation({
		mutationFn: completeMachineApi,
		onSuccess: () => queryClient.invalidateQueries({ queryKey: ['machines'] })
	});
	const deleteMachineMutation = useMutation({
		mutationFn: deleteMachineApi,
		onSuccess: () => queryClient.invalidateQueries({ queryKey: ['machines'] })
	});

	// UI state
	const [open, setOpen] = useState(false);
	const [selectedBlock, setSelectedBlock] = useState(null);
	const [form, setForm] = useState({ model: '', productNo: '' });
	const [snackbar, setSnackbar] = useState({ open: false, message: '', action: null });

	// Always 12 blocks, fill with machines or empty
	const blocks = Array.from({ length: BLOCKS }, (_, i) => {
		const machine = machines.find(m => m.blockNo === i + 1);
		return machine ? { ...machine, block: i + 1 } : { block: i + 1 };
	});

	const handleAddClick = (blockIdx) => {
		setSelectedBlock(blockIdx);
		setForm({ model: '', productNo: '' });
		setOpen(true);
	};

	const handleAddMachine = async () => {
		// 7-digit validation
		if (!form.model || !/^\d{7}$/.test(form.productNo)) {
			setSnackbar({ open: true, message: 'Enter model and unique 7-digit machine no.', action: null });
			return;
		}
		// Uniqueness validation
		const exists = machines.some(m => m.productNo === form.productNo);
		if (exists) {
			setSnackbar({ open: true, message: 'Machine no. must be unique!', action: null });
			return;
		}
		await addMachineMutation.mutateAsync({
			name: `M/C-${selectedBlock + 1}`,
			status: 'INCOMPLETE',
			completed: false,
			masterCardInfo: '',
			electricDrawingPath: '',
			machineNo: String(selectedBlock + 1),
			model: form.model,
			productNo: form.productNo,
			blockNo: selectedBlock + 1
		});
		setOpen(false);
		setSelectedBlock(null);
	};

	const markComplete = async (machine) => {
		await completeMachineMutation.mutateAsync(machine.id);
		setSnackbar({
			open: true,
			message: 'Would you like to remove this machine from shopfloor?',
			action: (
				<Button color="inherit" size="small" onClick={() => removeMachine(machine.id)}>
					Yes, Remove
				</Button>
			),
		});
	};

	const removeMachine = async (id) => {
		await deleteMachineMutation.mutateAsync(id);
		setSnackbar({ open: true, message: 'Machine removed from shopfloor and added to completed table.', action: null });
	};

	return (
		<>
			<Grid container spacing={2} sx={{ mt: 2, background: '#fff', minHeight: '80vh', p: 2 }}>
				{blocks.map((m, idx) => (
					<Grid item xs={6} sm={4} md={3} key={idx}>
						<Card sx={{ background: m.completed ? '#c8e6c9' : (m.status === 'INCOMPLETE' ? '#ffcdd2' : '#fff'), minHeight: 120 }}>
							<CardContent sx={{ textAlign: 'center' }}>
								<Typography variant="h6">Machine No. {m.block}</Typography>
								{m.id ? (
									<>
										<Typography variant="subtitle1">Model: {m.model}</Typography>
										<Typography variant="subtitle2">Product No.: {m.productNo}</Typography>
										<Button variant="contained" onClick={() => navigate(`/machine/${m.id}`)} sx={{ mt: 1 }}>View Details</Button>
										{!m.completed && (
											<Button variant="outlined" color="warning" sx={{ mt: 1 }} onClick={() => markComplete(m)}>Mark as Complete</Button>
										)}
									</>
								) : (
									<Button variant="outlined" color="primary" sx={{ mt: 2 }} onClick={() => handleAddClick(idx)}>Add Machine</Button>
								)}
							</CardContent>
						</Card>
					</Grid>
				))}
			</Grid>
			<Dialog open={open} onClose={() => setOpen(false)}>
				<DialogTitle>Add Machine</DialogTitle>
				<DialogContent>
					<TextField label="Model" value={form.model} onChange={e => setForm({ ...form, model: e.target.value })} fullWidth margin="normal" autoFocus />
					<TextField label="Machine No. (7 digits)" value={form.productNo} onChange={e => setForm({ ...form, productNo: e.target.value })} fullWidth margin="normal" inputProps={{ maxLength: 7 }} />
				</DialogContent>
				<DialogActions>
					<Button onClick={() => setOpen(false)}>Cancel</Button>
					<Button onClick={handleAddMachine} variant="contained">Add</Button>
				</DialogActions>
			</Dialog>
			<Snackbar open={snackbar.open} autoHideDuration={6000} onClose={() => setSnackbar({ ...snackbar, open: false })} message={snackbar.message} action={snackbar.action} />
		</>
	);
}

export default MachineGrid;
