import React, { useEffect, useState } from 'react';
import { Grid, Card, CardContent, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function MachineGrid() {
  const [machines, setMachines] = useState([]);
  const navigate = useNavigate();
  useEffect(() => {
    axios.get('/api/machines').then(res => setMachines(res.data));
  }, []);
  return (
    <Grid container spacing={2} sx={{ mt: 2 }}>
      {machines.map(m => (
        <Grid item xs={6} sm={4} md={3} key={m.id}>
          <Card sx={{ background: m.completed ? '#c8e6c9' : (m.status === 'INCOMPLETE' ? '#ffcdd2' : '#fff') }}>
            <CardContent>
              <Typography variant="h6">{m.name}</Typography>
              <Button variant="contained" onClick={() => navigate(`/machine/${m.id}`)} sx={{ mt: 1 }}>View Details</Button>
              {m.completed ? <Typography color="success.main">Completed</Typography> : <Typography color="error.main">Incomplete</Typography>}
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
export default MachineGrid;
